namespace Amatsukaze.ScriptTest.零式.M4S.门神
{
    public class 魔女回翔 : ITriggerScript
    {
        private Vector3 position1 = new Vector3(94.5f, 0, 87.8f); //横二安全雷线靠左
        private Vector3 position2 = new Vector3(94.5f, 0, 92.8f); //横一安全雷线靠左
        private Vector3 position3 = new Vector3(97.5f, 0, 93f); //横一安全雷线靠右
        private Vector3 position4 = new Vector3(97.5f, 0, 87.8f); //横二安全雷线靠右

        private bool firstSafe = false;
        private bool secondSafe = false;

        private bool thunderRight = false;
        private bool thunderLeft = false;

        private bool sideSafe = false;
        private bool midSafe = false;

        private float offset = 0.2f;
        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {

            if (!scriptEnv.KV.ContainsKey("先中间雷线"))
            {
                scriptEnv.KV.Add("先中间雷线", sideSafe);
            }
            else if (!scriptEnv.KV.ContainsKey("先两边雷线"))
            {
                scriptEnv.KV.Add("先两边雷线", midSafe);
            }



            if (condParams is EnemyCastSpellCondParams spell)
            {

                if (spell.SpellId == 38513)
                {
                    thunderRight = true;

                    LogHelper.Print("雷线靠右");
                }
                else if (spell.SpellId == 36335)
                {
                    thunderLeft = true;

                    LogHelper.Print("雷线靠左");
                }
            }
            else if (condParams is UnitCreateCondParams unit)
            {
                if (unit.BattleChara.DataId == 17324 && unit.BattleChara.Position.Z >= 87 && unit.BattleChara.Position.Z <= 88)  //第一排安全
                {

                    firstSafe = true;
                    LogHelper.Print("第二横排危险");

                }
                else if (unit.BattleChara.DataId == 17324 && unit.BattleChara.Position.Z >= 82 && unit.BattleChara.Position.Z <= 83)  //第二排安全
                {

                    secondSafe = true;
                    LogHelper.Print("第一横排危险");

                }
            }


            if (thunderRight && secondSafe)
            {
                PositionHelper.MoveToPositionWithOffset(position4,offset);

                return true;
            }
            else if (thunderRight && firstSafe)
            {
                PositionHelper.MoveToPositionWithOffset(position3,offset);

                return true;
            }
            else if (thunderLeft && firstSafe)
            {
                PositionHelper.MoveToPositionWithOffset(position2,offset);

                return true;
            }
            else if (thunderLeft && secondSafe)
            {
                PositionHelper.MoveToPositionWithOffset(position1,offset);

                return true;
            }


            return false;
        }
    }

}

