                        {
                            PositionHelper.MoveToPositionWithOffset(position2, offset);

                        }
                        else if ((bool)scriptEnv.KV["先两边雷线"])
                        {
                            PositionHelper.MoveToPositionWithOffset(position4, offset);

                        }

                        return true;
                    }
                }
                else
                {
                    if (Core.Resolve<MemApiBuff>().BuffStackCount(boss, 2970u) == 246)
                    {
                        LogHelper.Print($"层数: {Core.Resolve<MemApiBuff>().BuffStackCount(boss, 2970u)}");
