namespace Amatsukaze.ScriptTest.零式.M4S.门神
{
    public class 钢铁月环引导 : ITriggerScript
    {
        private Vector3 position1 = new Vector3(91f, 0, 91f); //钢铁近
        private Vector3 position2 = new Vector3(90, 0, 90); //钢铁远
        private Vector3 position3 = new Vector3(93.7f, 0, 93.7f); //月环近
        private Vector3 position4 = new Vector3(93.3f, 0, 93.3f); //月环远

        private Vector3 position5 = new Vector3(100, 0, 107); //C

        private float offset = 0;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            if (condParams is not EnemyCastSpellCondParams spell)
            {
                return false;
            }

            if (spell.SpellId == 38369)
            {
                LogHelper.Print("月环，钢铁，月环，钢铁");

                月环();

                return true;
            }
            else if (spell.SpellId == 38368)
            {
                LogHelper.Print("钢铁，月环，钢铁，月环");

                钢铁();

                return true;
            }

            return false;
        }

        private async Task 钢铁()
        {
            PositionHelper.MoveToPositionWithOffset(position1,offset);


            await Coroutine.Instance.WaitAsync(15000); //76

            PositionHelper.MoveToPositionWithOffset(position3,offset);


            await Coroutine.Instance.WaitAsync(4000); //80

            PositionHelper.MoveToPositionWithOffset(position2,offset);


            await Coroutine.Instance.WaitAsync(3500); //83.5

            PositionHelper.MoveToPositionWithOffset(position4,offset);


            await Coroutine.Instance.WaitAsync(4000);

            PositionHelper.MoveToPositionWithOffset(position5,offset);



        }

        private async Task 月环()
        {
            PositionHelper.MoveToPositionWithOffset(position3,offset);


            await Coroutine.Instance.WaitAsync(15000); //76

            PositionHelper.MoveToPositionWithOffset(position1,offset);


            await Coroutine.Instance.WaitAsync(4000); //80

            PositionHelper.MoveToPositionWithOffset(position4,offset);


            await Coroutine.Instance.WaitAsync(3500); //83.5

            PositionHelper.MoveToPositionWithOffset(position2,offset);


            await Coroutine.Instance.WaitAsync(4000);

            PositionHelper.MoveToPositionWithOffset(position5,offset);



        }
    }
}

using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.CombatRoutine.Trigger;
