namespace Amatsukaze.ScriptTest.零式.M4S.门神
{


    public class 雷转第一次 : ITriggerScript
    {
        private Vector3 squareTopLeft = new Vector3();
        private Vector3 squareTopRight = new Vector3();
        private Vector3 squareBottomLeft = new Vector3();
        private Vector3 squareBottomRight = new Vector3();

        private Vector3 safeTopLeftStay = new Vector3(95,0,82.5f); //分摊点位
        private Vector3 safeTopRightStay = new Vector3(117.5f,0,95);
        private Vector3 safeBottomLeftStay = new Vector3(82.5f,0,105);
        private Vector3 safeBottomRightStay = new Vector3(105,0,117);

        private Vector3 safeTopLeftSplit = new Vector3(99,0,82.5f); //分散点位
        private Vector3 safeTopRightSplit = new Vector3(117,0,99);
        private Vector3 safeBottomLeftSplit = new Vector3(82.5f,0,100.5f);
        private Vector3 safeBottomRightSplit = new Vector3(100.5f,0,117);

        private int stl = 0;
        private int str = 0;
        private int sbl = 0;
        private int sbr = 0;

        private bool 分散 = false;
        private bool 分摊 = false;
        private bool 左刀 = false;
        private bool 右刀 = false;

        private float offset = 0;

        private IBattleChara boss = null;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {

            if(Core.Me.GetCurrTarget() != null && Core.Me.GetCurrTarget().IsBoss())
            {
                boss = Core.Me.GetCurrTarget();
            }

            if (boss != null && boss.HasAura(2970))
            {
                if(Core.Resolve<MemApiBuff>().BuffStackCount(boss, 2970u) == 240)
                {

                    分摊 = true;
                }
                else if(Core.Resolve<MemApiBuff>().BuffStackCount(boss, 2970u) == 241)
                {

                    分散 = true;
                }

            }         

            if (condParams is UnitCreateCondParams unit)
            {
                if(unit.BattleChara.DataId == 17325)
                {
                    if(unit.BattleChara.Position.X == 85 && unit.BattleChara.Position.Z == 85) //左上角
                    {
                        squareTopLeft = unit.BattleChara.Position;
                    }
                    else if(unit.BattleChara.Position.X == 115 && unit.BattleChara.Position.Z == 85) //右上角
                    {
                        squareTopRight = unit.BattleChara.Position;
                    }
                    else if (unit.BattleChara.Position.X == 85 && unit.BattleChara.Position.Z == 115) //左下角
                    {
                        squareBottomLeft = unit.BattleChara.Position;
                    }
                    else if (unit.BattleChara.Position.X == 115 && unit.BattleChara.Position.Z == 115) //右下角
                    {
                        squareBottomRight = unit.BattleChara.Position;
                    }

                }
            }
            else if(condParams is ReceviceAbilityEffectCondParams abEffect)
            {
                if(abEffect.ActionId == 38343 && abEffect.Target.DataId == 17325)
                {
                    if(abEffect.Target.Position.X == 85 && abEffect.Target.Position.Z == 85) //左上角
                    {
                        stl++;
                    }
                    else if(abEffect.Target.Position.X == 115 && abEffect.Target.Position.Z == 85) //右上角
                    {
                        str++;
                    }
                    else if (abEffect.Target.Position.X == 85 && abEffect.Target.Position.Z == 115) //左下角
                    {
                        sbl++;
                    }
                    else if (abEffect.Target.Position.X == 115 && abEffect.Target.Position.Z == 115) //右下角
                    {
                        sbr++;
                    }
                }
            }           
            else if(condParams is EnemyCastSpellCondParams spell)
            {
                if(spell.SpellId == 38380) //右刀
                {
                    LogHelper.Print("即将右刀");
                    右刀 = true;
                }
                else if(spell.SpellId == 38381) //左刀
                {
                    LogHelper.Print("即将左刀");
                    左刀 = true;
                }
            }

            if (stl == 2 && (str == 2 || sbr == 2)&& 右刀 && 分摊)
            {
                LogHelper.Print("左下角安全区，即将分摊");
                PositionHelper.MoveToPositionWithOffset(safeBottomLeftStay,offset);

                return true;
            }
            else if(stl == 2 && (str == 2 || sbr == 2) && 右刀 && 分散)
            {
                LogHelper.Print("左下角安全区，即将分散");
                PositionHelper.MoveToPositionWithOffset(safeBottomLeftSplit,offset);

                return true;
            }
            else if ((stl == 2 || sbl == 2) && str == 2 && 左刀 && 分摊)
            {
                LogHelper.Print("右下角安全区，即将分摊");
                PositionHelper.MoveToPositionWithOffset(safeBottomRightStay,offset);

                return true;
            }
            else if ((stl == 2 || sbl == 2) && str == 2 && 左刀 && 分散)
            {
                LogHelper.Print("右下角安全区，即将分散");
                PositionHelper.MoveToPositionWithOffset(safeBottomRightSplit,offset);

                return true;
            }
            else if (sbl == 2 && (sbr == 2 || str == 2) && 右刀 && 分摊)
            {
                LogHelper.Print("左上角安全区，即将分摊");
                PositionHelper.MoveToPositionWithOffset(safeTopLeftStay,offset);

                return true;
            }
            else if (sbl == 2 && (sbr == 2 || str == 2) && 右刀 && 分散)
            {
                LogHelper.Print("左上角安全区，即将分散");
                PositionHelper.MoveToPositionWithOffset(safeTopLeftSplit,offset);

                return true;
            }
            else if ((sbl == 2 || stl == 2) && sbr == 2 && 左刀 && 分摊)
            {
                LogHelper.Print("右上角安全区，即将分摊");
                PositionHelper.MoveToPositionWithOffset(safeTopRightStay,offset);

                return true;
            }
            else if ((sbl == 2 || stl == 2) && sbr == 2 && 左刀 && 分散)
            {
                LogHelper.Print("右上角安全区，即将分散");
                PositionHelper.MoveToPositionWithOffset(safeTopRightSplit,offset);

                return true;
            }


            return false;
        }
    }
}

