namespace Amatsukaze.ScriptTest.零式.M4S.门神
{


    public class 雷转第二次_分散分摊 : ITriggerScript
    {


        private Vector3 右刀分摊3 = new Vector3(99f, 0, 100);
        private Vector3 右刀分摊2 = new Vector3(99f, 0, 112);
        private Vector3 左刀分摊3 = new Vector3(101f, 0, 100);
        private Vector3 左刀分摊2 = new Vector3(101f, 0, 112);

        private Vector3 右刀分散 = new Vector3(99f, 0, 82);
        private Vector3 左刀分散 = new Vector3(101f, 0, 82);

        private bool 分散 = false;
        private bool 分摊 = false;
        private bool 左刀 = false;
        private bool 右刀 = false;

        private float offset = 0.5f;

        private IBattleChara boss = null;


        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {

            if (Core.Me.GetCurrTarget() != null && Core.Me.GetCurrTarget().IsBoss())
            {
                boss = Core.Me.GetCurrTarget();
            }

            if (boss != null && boss.HasAura(2970))
            {
                if (Core.Resolve<MemApiBuff>().BuffStackCount(boss, 2970u) == 240)
                {

                    分摊 = true;
                }
                else if (Core.Resolve<MemApiBuff>().BuffStackCount(boss, 2970u) == 241)
                {

                    分散 = true;
                }

            }


            if (condParams is EnemyCastSpellCondParams spell)
            {
                if (spell.SpellId == 38380) //右刀
                {
                    LogHelper.Print("即将右刀");
                    右刀 = true;
                }
                else if (spell.SpellId == 38381) //左刀
                {
                    LogHelper.Print("即将左刀");
                    左刀 = true;
                }
            }


            if(右刀 && 分摊 && (((bool)scriptEnv.KV["长Buff"] && (int)scriptEnv.KV["电数"] ==1) || ((bool)scriptEnv.KV["短Buff"] && (int)scriptEnv.KV["电数"] == 2))) //少分摊
            {
                LogHelper.Print("右刀+少电层数分摊");
                PositionHelper.MoveToPositionWithOffset(右刀分摊2,offset);

                return true;
            }
            else if(右刀 && 分摊 && (((bool)scriptEnv.KV["长Buff"] && (int)scriptEnv.KV["电数"] == 2) || ((bool)scriptEnv.KV["短Buff"] && (int)scriptEnv.KV["电数"] == 3))) //多分摊
            {
                LogHelper.Print("右刀+多电层数分摊");
                PositionHelper.MoveToPositionWithOffset(右刀分摊3,offset);

                return true;
            }
            else if(右刀 && 分散)
            {
                LogHelper.Print("右刀+分散");
                PositionHelper.MoveToPositionWithOffset(右刀分散,offset);

                return true;

            }
            else if(左刀 && 分摊 && (((bool)scriptEnv.KV["长Buff"] && (int)scriptEnv.KV["电数"] == 1) || ((bool)scriptEnv.KV["短Buff"] && (int)scriptEnv.KV["电数"] == 2))) //少分摊
            {
                LogHelper.Print("左刀+少电层数分摊");
                PositionHelper.MoveToPositionWithOffset(左刀分摊2,offset);

                return true;
            }
            else if(左刀 && 分摊 && (((bool)scriptEnv.KV["长Buff"] && (int)scriptEnv.KV["电数"] == 2) || ((bool)scriptEnv.KV["短Buff"] && (int)scriptEnv.KV["电数"] == 3))) //多分摊
            {
                LogHelper.Print("左刀+多电层数分摊");
                PositionHelper.MoveToPositionWithOffset(左刀分摊3,offset);

                return true;
            }
            else if(左刀 && 分散)
            {
                LogHelper.Print("左刀+分散");
                PositionHelper.MoveToPositionWithOffset(左刀分散,offset);

                return true;
            }

            return false;
        }
    }
}

