namespace Amatsukaze.ScriptTest.零式.M4S.门神
{


    public class 雷转第二次后分摊 : ITriggerScript
    {

        IGameObject iconMember = null;
        private int count = 0;
        private Vector3 afk = new Vector3(97, 0, 100);

        private float offset = 2f;
        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
           if(condParams is TargetIconEffectCondParams targetIcon)
           {
                if (targetIcon.Args0 == -155)
                {
                    iconMember = targetIcon.Target;
                    LogHelper.Print($"分摊点名: {targetIcon.Target.Name}");
                    贴贴(targetIcon.Target);
                }
           }
           else if(condParams is ReceviceAbilityEffectCondParams ae)
            {
                if(ae.ActionId == 38382 && ae.Target == Core.Me)
                {
                    count++;
                }
            }


           if(count == 5)
            {
                PositionHelper.MoveToPositionWithOffset(afk,offset);

                return true;
            }


           return false;
        }

        private async Task 贴贴(IGameObject member)
        {
            LogHelper.Print($"正在去贴贴{member.Name}的路上");
            while(!MCHSettings.Instance.noAutoMove && count != 5 && AI.Instance.BattleData.CurrBattleTimeInMs != 0)
            {
                PositionHelper.MoveToPositionWithOffset(member.Position,offset);


                await Coroutine.Instance.WaitAsync(1000);

            }           


        }
    }
}

