using AEAssist.Extension;
using Amatsukaze.ScriptTest.Helpers;

namespace Amatsukaze.ScriptTest.零式.M4S.门神
{
    public class M4S场地炮 : ITriggerScript
    {
        private Vector3 leftSide = new Vector3(85, 0, 95);
        private Vector3 rightSide = new Vector3(115, 0, 95);

        private float offset = 1.5f;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {


            if (condParams is not ReceviceAbilityEffectCondParams spell)
            {
                return false;
            }

            if(spell.ActionId == 38355)
            {
                LogHelper.Print("炮击右场地,最左边安全");
                PositionHelper.MoveToPositionWithOffset(leftSide,offset);

                scriptEnv.KV["左场地"] = true;
                return true;
            }
            else if(spell.ActionId == 38354)
            {
                LogHelper.Print("炮击左场地,最右边安全");
                PositionHelper.MoveToPositionWithOffset(rightSide,offset);

                scriptEnv.KV["右场地"] = true;
                return true;
            }


            return false;
        }
    }
}

