namespace Amatsukaze.ScriptTest.零式.M4S.门神
{
    public class M4S三连炮Buff2 : ITriggerScript
    {
        private Vector3 leftSide = new Vector3(85, 0, 95);
        private Vector3 rightSide = new Vector3(115, 0, 95);

        private float offset = 0.5f;

        private uint thunderBuff = 4006u; //站boss圈外一点
        private uint currentBuff1 = 4002u; //站boss圈内
        private uint currentBuff2 = 4003u;
        private uint conductorBuff1 = 4005u; //站场内的那边
        private uint conductorBuff2 = 4004u;
        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {



            if (Core.Me.HasAura(thunderBuff))
            {
                LogHelper.Print("获得蓄雷Buff");
                scriptEnv.KV["蓄雷"] = true;
                return true;
            }
            else if(Core.Me.HasAura(currentBuff1) || Core.Me.HasAura(currentBuff2))
            {
                LogHelper.Print("获得远近雷Buff");
                scriptEnv.KV["远近雷"] = true;
                return true;
            }
            else if (Core.Me.HasAura(conductorBuff1) || Core.Me.HasAura(conductorBuff2))
            {
                LogHelper.Print("获得大小圈Buff");
                scriptEnv.KV["大小圈"] = true;
                return true;
            }

            return false;
        }
    }
}

