namespace Amatsukaze.ScriptTest.零式.M4S.门神
{
    public class M4S门神黄蓝炮 : ITriggerScript
    {
        private Vector3 leftTop = new Vector3(85, 0, 92.5f);
        private Vector3 leftBottom = new Vector3(85, 0, 107.5f);

        private Vector3 rightTop = new Vector3(115, 0, 92.5f);
        private Vector3 rightBottom = new Vector3(115, 0, 107.5f);



        private float offset = 0.5f;


        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            if (!scriptEnv.KV.ContainsKey("黄Buff"))
            {
                scriptEnv.KV.Add("黄Buff", false);
            }
            if (!scriptEnv.KV.ContainsKey("蓝Buff"))
            {
                scriptEnv.KV.Add("蓝Buff", false);
            }
            if (!scriptEnv.KV.ContainsKey("左场地"))
            {
                scriptEnv.KV.Add("左场地", false);
            }
            if (!scriptEnv.KV.ContainsKey("右场地"))
            {
                scriptEnv.KV.Add("右场地", false);
            }
            if (!scriptEnv.KV.ContainsKey("远近雷"))
            {
                scriptEnv.KV.Add("远近雷", false);
            }
            if (!scriptEnv.KV.ContainsKey("大小圈"))
            {
                scriptEnv.KV.Add("大小圈", false);
            }
            if (!scriptEnv.KV.ContainsKey("蓄雷"))
            {
                scriptEnv.KV.Add("蓄雷", false);
            }

            if (condParams is EnemyCastSpellCondParams spell)
            {
                if(spell.SpellId == 38361 && (spell.CastRot is > 3 and < 4 || spell.CastRot is > -4 and < -3) && (bool)scriptEnv.KV["左场地"] && (bool)scriptEnv.KV["黄Buff"])
                {
                    LogHelper.Print("左场地，上面蓝炮");
                    PositionHelper.MoveToPositionWithOffset(leftTop, offset);
                    return true;
                }
                else if(spell.SpellId == 38361 && spell.CastRot is > -1 and < 1 && (bool)scriptEnv.KV["左场地"] && (bool)scriptEnv.KV["黄Buff"])
                {
                    LogHelper.Print("左场地，下面蓝炮");
                    PositionHelper.MoveToPositionWithOffset(leftBottom, offset);
                    return true;
                }
                else if(spell.SpellId == 38360 && spell.CastRot is > -1 and < 1 && (bool)scriptEnv.KV["左场地"] && (bool)scriptEnv.KV["蓝Buff"])
                {
                    LogHelper.Print("左场地，下面黄炮");
                    PositionHelper.MoveToPositionWithOffset(leftBottom, offset);
                    return true;
                }
                else if(spell.SpellId == 38360 && (spell.CastRot is > 3 and < 4 || spell.CastRot is > -4 and < -3) && (bool)scriptEnv.KV["左场地"] && (bool)scriptEnv.KV["蓝Buff"])
                {
                    LogHelper.Print("左场地，上面黄炮");
                    PositionHelper.MoveToPositionWithOffset(leftTop, offset);
                    return true;
                }
                else if (spell.SpellId == 38361 && (spell.CastRot is > 3 and < 4 || spell.CastRot is > -4 and < -3) && (bool)scriptEnv.KV["右场地"] && (bool)scriptEnv.KV["黄Buff"])
                {
                    LogHelper.Print("右场地，上面蓝炮");
                    PositionHelper.MoveToPositionWithOffset(rightTop, offset);
                    return true;
                }
                else if (spell.SpellId == 38361 && spell.CastRot is > -1 and < 1 && (bool)scriptEnv.KV["右场地"] && (bool)scriptEnv.KV["黄Buff"])
                {
                    LogHelper.Print("右场地，下面蓝炮");
                    PositionHelper.MoveToPositionWithOffset(rightBottom, offset);
                    return true;
                }
                else if (spell.SpellId == 38360 && spell.CastRot is > -1 and < 1 && (bool)scriptEnv.KV["右场地"] && (bool)scriptEnv.KV["蓝Buff"])
                {
                    LogHelper.Print("右场地，下面黄炮");
                    PositionHelper.MoveToPositionWithOffset(rightBottom, offset);
                    return true;
                }
                else if (spell.SpellId == 38360 && (spell.CastRot is > 3 and < 4 || spell.CastRot is > -4 and < -3) && (bool)scriptEnv.KV["右场地"] && (bool)scriptEnv.KV["蓝Buff"])
                {
                    LogHelper.Print("右场地，上面黄炮");
                    PositionHelper.MoveToPositionWithOffset(rightTop, offset);
                    return true;
                }
            }

            return false;
        }
    }
}

