namespace Amatsukaze.ScriptTest.零式.M4S.门神
{
    public class M4S门神黄蓝炮结束 : ITriggerScript
    {

        private Vector3 toMiddle = new Vector3(100, 0, 105);

        private float offset = 1.5f;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {

            if(condParams is not EnemyCastSpellCondParams spell)
            {
                return false;
            }

            if(spell.SpellId == 38384)
            {
                前往中心();
                return true;
            }


            return false;
        }

        private async Task 前往中心()
        {
            await Coroutine.Instance.WaitAsync(1000);

            LogHelper.Print("三连炮结束，前往中心");
            PositionHelper.MoveToPositionWithOffset(toMiddle, offset);
        }
    }
}

using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.CombatRoutine.Trigger;
using AEAssist;
using System;
using System.Collections.Generic;
using System.Numerics;
using AEAssist.Helper;
using AEAssist.MemoryApi;
using AEAssist.Extension;
