namespace Amatsukaze.ScriptTest.零式.M4S.门神
{
    public class M4S水波 : ITriggerScript
    {

        private Vector3 left = new Vector3(108, 0, 103.5f); //近 正点
        private Vector3 leftLast = new Vector3(112, 0, 105); //远 正点

        private Vector3 right = new Vector3(104f, 0, 104f); //近 非正
        private Vector3 rightLast = new Vector3(110.5f, 0, 110.5f); //远 非正

        private Vector3 heal = new Vector3(103.5f, 0, 103.5f); //吃奶

        private Vector3 knock = new Vector3(100, 0, 107); //击退

        private float offset = 0.2f;

        private bool 水波 = false;

        private bool isMe = false;

        private int 水波count = 0;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            if(condParams is GameLogCondParams logCondParams)
            {
                if (logCondParams.Log == "只能用雷转质了……")
                {
                    LogHelper.Print("前往水波提前站位");
                    PositionHelper.MoveToPositionWithOffset(left, offset);
                }
            }
            else if(condParams is EnemyCastSpellCondParams spell)
            {
                if(spell.SpellId == 37118 && !水波)
                {
                    水波 = true;
                    LogHelper.Print("即将水波!");
                    水波自动();
                }
            }
            else if(condParams is ReceviceAbilityEffectCondParams ae)
            {

                if(ae.ActionId == 39118 && ae.Target == Core.Me)
                {
                    isMe = true;
                }

            }

            if(水波count == 2)
            {
                LogHelper.Print("两次水波结束，前往Boss正面准备被击飞");
                PositionHelper.MoveToPositionWithOffset(knock, offset);
                return true;
            }

            return false;
        }

        private async Task 水波自动()
        {
            //320.5
            PositionHelper.MoveToPositionWithOffset(left, offset); //第一次水波站位
            await Coroutine.Instance.WaitAsync(3500); //324

            PositionHelper.MoveToPositionWithOffset(right, offset); //第一次水波判定

            await Coroutine.Instance.WaitAsync(3000); //327

            PositionHelper.MoveToPositionWithOffset(left, offset); //第二次水波判定

            await Coroutine.Instance.WaitAsync(3000); //330

            PositionHelper.MoveToPositionWithOffset(right, offset); //第三次水波判定

            await Coroutine.Instance.WaitAsync(3000); //333

            PositionHelper.MoveToPositionWithOffset(leftLast, offset); //第四次水波判定

            await Coroutine.Instance.WaitAsync(3000); //336.5

            if(!isMe)
            {
                LogHelper.Print("前往挡枪!");
                PositionHelper.MoveToPositionWithOffset(right, offset); //第五次水波判定
            }
            else if(isMe)
            {
                LogHelper.Print("让队友挡枪");
                PositionHelper.MoveToPositionWithOffset(rightLast, offset); //第五次水波判定
            }

            await Coroutine.Instance.WaitAsync(3000); //339.5

            if (!isMe)
            {

                PositionHelper.MoveToPositionWithOffset(left, offset); //第六次水波判定
            }
            else if (isMe)
            {

                PositionHelper.MoveToPositionWithOffset(leftLast, offset); //第六次水波判定
            }



            await Coroutine.Instance.WaitAsync(3000); //342.5

            PositionHelper.MoveToPositionWithOffset(heal, offset); //水波结束

            isMe = false;
            水波 = false;
            水波count++;

        }
    }
}

