    public class m4s初始化 : ITriggerScript
    {
        // 使用事件(True)，则脚本逻辑由Check方法控制。Check返回True代表此节点执行完毕

        private string mapBaseName = Core.Resolve<MemApiZoneInfo>().GetCurrZoneInfo().MapBaseName;
        RectShape safeArea = new RectShape()
        {
            First = new Vector3(80, 0, 150),      // 左上角
            Second = new Vector3(80, 0, 180),    // 左下角
            Third = new Vector3(120, 0, 180),    // 右下角
            Fourth = new Vector3(120, 0, 150),    // 右上角
            Origin = new Vector3(100, 0, 165)    // 中心点
        };

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {

            if (AvoidManager.Instance.LoadMap(mapBaseName, 0.2f, out var map))
            {

                LogHelper.Print("加载M4S本体地图成功");
                map.AgentRadius = 0.5f;
                map.AddDangerShape(10086, new DangerShape(0, 0, new SafeRectShape() { RectShape = safeArea, Origin = new Vector3(100, 0, 165) }));



            }


            return true;

        }
    }

}

