    public class 我润我润 : ITriggerScript
    {
        // 使用事件(True)，则脚本逻辑由Check方法控制。Check返回True代表此节点执行完毕
        private string mapBaseName = Core.Resolve<MemApiZoneInfo>().GetCurrZoneInfo().MapBaseName;
        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {

            if (AvoidManager.Instance.GetMap(mapBaseName, out var map))
            {


                if (!map.IsPosSafe(Core.Me.Position))
                {
                    if (map.FindNearestSafePoint(Core.Me.Position, 100, out var safePoint))
                    {
                        PositionHelper.MoveToPositionWithOffset(safePoint, 0);
                    }
                }

            }






            return false;
        }


    }
}

