        private Vector3 position1 = new Vector3(80.5f, 0, 151.5f);
        private Vector3 position2 = new Vector3(82f, 0, 160);
        private Vector3 position3 = new Vector3(82f, 0, 170); //第三个安全


        private bool 好疼啊 = false;

        private int firstFire = 0; //1为左2为右
        private int secondFire = 0;
        private int thirdFire = 0;

        private bool startCheck = false;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            if(condParams is EnemyCastSpellCondParams spell)
            {
                if(spell.SpellId == 38389 && spell.CastPos.X is > 114.5f and < 115.5f && spell.CastPos.Z is > 149.5f and < 150.5f) //右边第一个地火
                {
                    if(spell.CastRot is > 1.5f and < 1.6f)
                    {
                        firstFire = 2;
                    }
                    else if(spell.CastRot is > -1.6f and < -1.5f)
                    {
                        firstFire = 1;
                    }
                }
                else if (spell.SpellId == 38389 && spell.CastPos.X is > 114.5f and < 115.5f && spell.CastPos.Z is > 159.5f and < 160.5f) //右边第二个地火
                {
                    if (spell.CastRot is > 1.5f and < 1.6f)
                    {
                        secondFire = 2;
                    }
                    else if (spell.CastRot is > -1.6f and < -1.5f)
                    {
                        secondFire = 1;
                    }
                }
                else if (spell.SpellId == 38389 && spell.CastPos.X is > 114.5f and < 115.5f && spell.CastPos.Z is > 169.5f and < 170.5f) //右边第三个地火
                {
                    if (spell.CastRot is > 1.5f and < 1.6f)
                    {
                        thirdFire = 2;
                    }
                    else if (spell.CastRot is > -1.6f and < -1.5f)
                    {
                        thirdFire = 1;
                    }
                }
            }
            else if(condParams is ReceviceAbilityEffectCondParams ae)
            {
                if(ae.ActionId == 38392)
                {
                    好疼啊 = true;
                }
            }

            if((firstFire == 2 || secondFire == 2 || thirdFire == 2) && !startCheck)
            {
                startCheck = true;
                PositionHelper.MoveToPositionWithOffset(startPosition, 0);
            }


            if(firstFire == 2 && 好疼啊)
            {
                LogHelper.Print("第一个地火位置安全");
                moveToSafe(position1);
                return true;
            }
            else if(secondFire == 2 && 好疼啊)
            {
                LogHelper.Print("第二个地火位置安全");
                moveToSafe(position2);
                return true;
            }
            else if (thirdFire == 2 && 好疼啊)
            {
                LogHelper.Print("第三个地火位置安全");
                moveToSafe(position3);
                return true;
            }

            return false;
        }

        private async Task moveToSafe(Vector3 position)
        {
            await Coroutine.Instance.WaitAsync(0);
            PositionHelper.MoveToPositionWithOffset(position, 0);
        }
    }

}

