using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.CombatRoutine.Trigger;
using AEAssist;
using System;
using System.Collections.Generic;
using System.Numerics;
using AEAssist.Helper;
using AEAssist.MemoryApi;
using AEAssist.Extension;
using Amatsukaze.ScriptTest.Helpers;
using Dalamud.Game.ClientState.Objects.Types;
using Amatsukaze.MCH.Settings;
using AEAssist.CombatRoutine.Module;

namespace Amatsukaze.ScriptTest.零式.M4S.本体
{
    public class M4S爆弹检测 : ITriggerScript
    {

        private Vector3 mid = new Vector3(100, 0, 168.5f);

        private Vector3 mtPosition = new Vector3(95, 0, 155);
        private Vector3 stPosition = new Vector3(105, 0, 155);
        private Vector3 stPosition2 = new Vector3(102.5f, 0, 162.5f);

        private IGameObject mt = null;
        private IGameObject st = null;
        private IGameObject d1HasBuff = null;
        private IGameObject d1NoBuff = null;

        private int buffCount = 0;

        private bool check = false;
        private bool tookSTBuff = false;
        private bool tookMTBuff = false;

        private bool dkMT = false;
        private bool dkST = false;

        private bool hasBuff = false;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            if(condParams is ReceviceAbilityEffectCondParams ae)
            {
                if(ae.ActionId == 38431 && ae.Target.Position.X < 100) //接线判定
                {
                    mt = ae.Target;
                    LogHelper.Print($"MT名字: {mt.Name}, 位置X: {mt.Position.X}, 位置Z: {mt.Position.Z}");
                    var dk = PartyHelper.CastableAlliesWithin30.LastOrDefault(agent => (agent.HasAura(810u) || agent.HasAura(811u)) && agent == mt);
                    if (dk != null && !dkMT)
                    {
                        LogHelper.Print("dk有无敌");
                        dkMT = true;
                    }

                }
                else if(ae.ActionId == 38431 && ae.Target.Position.X > 100) //接线判定
                {
                    st = ae.Target;
                    LogHelper.Print($"ST名字: {mt.Name}, 位置X: {mt.Position.X}, 位置Z: {mt.Position.Z}");
                    var dk = PartyHelper.CastableAlliesWithin30.LastOrDefault(agent => (agent.HasAura(810u) || agent.HasAura(811u)) && agent == st);
                    if (dk != null && !dkST)
                    {
                        LogHelper.Print("dk有无敌");
                        dkST = true;
                    }

                }
                else if(ae.ActionId == 38432) //分散判定有buff
                {
                    if(ae.Target == Core.Me)
                    {
                        PositionHelper.MoveToPositionWithOffset(mid, 2f);
                        LogHelper.Print("有buff,中间集合");
                        buffCount = 0;
                        return true;
                    }
                    else if(ae.Target != Core.Me)
                    {

                        buffCount++;

                    }
                }
                else if(ae.ActionId == 38433 && ae.Target == Core.Me)
                {
                    PositionHelper.MoveToPositionWithOffset(mid, 2f);
                    LogHelper.Print("芥末爆弹结束");
                    hasBuff = false;
                    tookMTBuff = false;
                    tookSTBuff = false;

                    return true;
                }
            }



            if(buffCount == 4 && !check)
            {
                check = true;
            }

            if(check)
            {
                //LogHelper.Print("无buff，去贴贴接buff");

                d1HasBuff = PartyHelper.CastableAlliesWithin30.LastOrDefault(agent => agent.Position.X < 90 && agent.Position.Z < 160 && agent.HasAura(2937u));
                d1NoBuff = PartyHelper.CastableAlliesWithin30.LastOrDefault(agent => agent.Position.X < 90 && agent.Position.Z < 160 && !agent.HasAura(2937u));

                if (d1HasBuff == null && d1NoBuff == null && !tookMTBuff)
                {
                    if(dkMT)
                    {
                        tookMTBuff = true;
                        LogHelper.Print("需要前往MT位置接线，但是MT是DK且开启无敌了，回到场中集合");
                        PositionHelper.MoveToPositionWithOffset(mid, 2f);
                        buffCount = 0;
                        check = false;
                        dkMT = false;
                        return true;
                    }
                    else
                    {
                        tookMTBuff = true;
                        Core.Resolve<MemApiChatMessage>().Toast2("无法找到D1,默认前往MT位置接线", 1, 5000);
                        buffCount = 0;
                        check = false;
                        findMT(mt);

                    }



                }
                else if (((d1HasBuff != null && d1HasBuff.IsDead) || (d1NoBuff != null && d1NoBuff.IsDead)) && !tookMTBuff)
                {
                    if (dkMT)
                    {
                        tookMTBuff = true;
                        LogHelper.Print("需要前往MT位置接线，但是MT是DK且开启无敌了，回到场中集合");
                        PositionHelper.MoveToPositionWithOffset(mid, 2f);
                        buffCount = 0;
                        check = false;
                        dkMT = false;
                        return true;
                    }
                    else
                    {
                        tookMTBuff = true;
                        Core.Resolve<MemApiChatMessage>().Toast2("D1已死亡,默认前往MT位置接线", 1, 5000);
                        buffCount = 0;
                        check = false;
                        findMT(mt);
                    }


                }
                else if(d1NoBuff != null && !tookSTBuff)
                {
                    if(dkST)
                    {
                        tookSTBuff = true;
                        LogHelper.Print("需要前往ST位置接线，但是ST是DK且开启无敌了，回到场中集合");
                        PositionHelper.MoveToPositionWithOffset(mid, 2f);
                        buffCount = 0;
                        check = false;
                        dkST = false;
                        return true;
                    }
                    else
                    {
                        tookSTBuff = true;
                        LogHelper.Print("D1也没有buff，去贴贴ST");
                        buffCount = 0;
                        check = false;
                        findST(st);

                    }


                }
                else if (d1HasBuff != null && !tookMTBuff)
                {
                    if (dkMT)
                    {
                        tookMTBuff = true;
                        LogHelper.Print("需要前往MT位置接线，但是MT是DK且开启无敌了，回到场中集合");
                        PositionHelper.MoveToPositionWithOffset(mid, 2f);
                        buffCount = 0;
                        check = false;
                        dkMT = false;
                        return true;
                    }
                    else
                    {
                        tookMTBuff = true;
                        LogHelper.Print("D1有buff，去贴贴MT");
                        buffCount = 0;
                        check = false;
                        findMT(mt);
                    }


                }



            }

            if(Core.Me.HasAura(4007u) && !hasBuff)
            {
                hasBuff = true;
                LogHelper.Print("已接到Buff，调整站位");
                if(tookMTBuff)
                {
                    PositionHelper.MoveToPositionWithOffset(mtPosition, 0.5f);
                }
                else if(tookSTBuff)
                {
                    PositionHelper.MoveToPositionWithOffset(stPosition, 0.5f);
                }
            }

            return false;
        }

        private async Task findMT(IGameObject member)
        {


            while(!Core.Me.HasAura(4007u) && !MCHSettings.Instance.noAutoMove && AI.Instance.BattleData.CurrBattleTimeInMs != 0)
            {
                PositionHelper.MoveToPositionWithOffset(member.Position, 0);


                await Coroutine.Instance.WaitAsync(1000);
            }
        }

        private async Task findST(IGameObject member)
        {
            PositionHelper.MoveToPositionWithOffset(stPosition2, 0.5f);

            while(Core.Me.Position.X < 101f && !MCHSettings.Instance.noAutoMove && AI.Instance.BattleData.CurrBattleTimeInMs != 0)
            {
                await Coroutine.Instance.WaitAsync(100);
            }

            while (!Core.Me.HasAura(4007u) && !MCHSettings.Instance.noAutoMove && AI.Instance.BattleData.CurrBattleTimeInMs != 0)
            {
                PositionHelper.MoveToPositionWithOffset(member.Position, 0);


                await Coroutine.Instance.WaitAsync(1000);
            }
        }


    }
}

