namespace Amatsukaze.ScriptTest.零式.M4S.本体
{
    public class M4S属性转换1 : ITriggerScript //需要提前站位
    {
        private Vector3 rightFireSafe = new Vector3(105.7f, 0, 152.5f);
        private Vector3 leftFireSafe = new Vector3(94.5f, 0, 152.5f);

        private Vector3 rightWaterSafe = new Vector3(109, 0, 165);
        private Vector3 leftWaterSafe = new Vector3(91, 0, 165);

        private Vector3 mid = new Vector3(100, 0, 165);

        private Vector3 踩塔 = new Vector3(85, 0, 175);

        private float offset = 0.5f;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            if(!scriptEnv.KV.ContainsKey("次数"))
            {
                scriptEnv.KV.Add("次数", 0);
            }

            if(condParams is EnemyCastSpellCondParams spell)
            {
                switch(spell.SpellId)
                {
                    case 38404:
                        if((int)scriptEnv.KV["次数"] == 0)
                        {
                            LogHelper.Print("火属性且先右刀后左刀，提前站位");
                            PositionHelper.MoveToPositionWithOffset(leftFireSafe, offset);

                        }
                        scriptEnv.KV["次数"] = (int)scriptEnv.KV["次数"] + 1;
                        break;
                    case 38408:
                        LogHelper.Print("火属性:先右刀后左刀");
                        火先右刀后左刀();
                        return true;
                    case 38402:
                        if ((int)scriptEnv.KV["次数"] == 0)
                        {
                            LogHelper.Print("火属性且先左刀后右刀，提前站位");
                            PositionHelper.MoveToPositionWithOffset(rightFireSafe, offset);

                        }
                        scriptEnv.KV["次数"] = (int)scriptEnv.KV["次数"] + 1;

                        break;
                    case 38406:
                        LogHelper.Print("火属性:先左刀后右刀");
                        火先左刀后右刀();
                        return true;
                    case 38405:
                        if ((int)scriptEnv.KV["次数"] == 0)
                        {
                            LogHelper.Print("水属性且先右后左，提前站位");
                            PositionHelper.MoveToPositionWithOffset(rightWaterSafe, 0);

                        }
                        scriptEnv.KV["次数"] = (int)scriptEnv.KV["次数"] + 1;
                        break;
                    case 38409:
                        LogHelper.Print("水属性:先右后左");
                        水先右后左();
                        return true;
                    case 38403:
                        if ((int)scriptEnv.KV["次数"] == 0)
                        {
                            LogHelper.Print("水属性且先左后右，提前站位");
                            PositionHelper.MoveToPositionWithOffset(leftWaterSafe, 0);

                        }
                        scriptEnv.KV["次数"] = (int)scriptEnv.KV["次数"] + 1;
                        break;
                    case 38407:
                        LogHelper.Print("水属性:先左后右");
                        水先左后右();
                        return true;

                }



            }

            if ((int)scriptEnv.KV["次数"] == 2)
            {
                LogHelper.Print("剑如雨下!");
                PositionHelper.MoveToPositionWithOffset(踩塔, 0.5f);
                scriptEnv.KV["次数"] = (int)scriptEnv.KV["次数"] + 1;
            }

            return false;
        }

        private async Task 火先左刀后右刀()
        {
            PositionHelper.MoveToPositionWithOffset(rightFireSafe, offset);
            await Coroutine.Instance.WaitAsync(6000);
            PositionHelper.MoveToPositionWithOffset(leftFireSafe, offset);
            await Coroutine.Instance.WaitAsync(5000);
            PositionHelper.MoveToPositionWithOffset(mid, 2);
        }

        private async Task 火先右刀后左刀()
        {

            PositionHelper.MoveToPositionWithOffset(leftFireSafe, offset);
            await Coroutine.Instance.WaitAsync(6000);
            PositionHelper.MoveToPositionWithOffset(rightFireSafe, offset);
            await Coroutine.Instance.WaitAsync(5000);
            PositionHelper.MoveToPositionWithOffset(mid, 2);
        }

        private async Task 水先左后右()
        {
            PositionHelper.MoveToPositionWithOffset(leftWaterSafe, 0);
            await Coroutine.Instance.WaitAsync(7000);
            PositionHelper.MoveToPositionWithOffset(rightWaterSafe, 0);
            await Coroutine.Instance.WaitAsync(5000);
            PositionHelper.MoveToPositionWithOffset(mid, 2);
        }

        private async Task 水先右后左()
        {
            PositionHelper.MoveToPositionWithOffset(rightWaterSafe, 0);
            await Coroutine.Instance.WaitAsync(7000);
            PositionHelper.MoveToPositionWithOffset(leftWaterSafe, 0);
            await Coroutine.Instance.WaitAsync(5000);
            PositionHelper.MoveToPositionWithOffset(mid, 2);
        }
    }
}

