using System.Drawing;



namespace Amatsukaze.ScriptTest.零式.M4S.本体
{
    public class M4S日落 : ITriggerScript
    {

        private string mapBaseName = Core.Resolve<MemApiZoneInfo>().GetCurrZoneInfo().MapBaseName;
        private Dictionary<uint, IGameObject> 分身 = new Dictionary<uint, IGameObject>();


        private int duration = 10000;
        private int lateDuration = 8000;

        private int delay = 10000;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            Random random = new Random();
            uint randomUint = (uint)random.Next(1, int.MaxValue - 100);

            //4566 先右
            //4567 后右
            //4568 先左
            //4569 后左
            if (AvoidManager.Instance.GetMap(mapBaseName, out var map))
            {
                if (condParams is ActorControlCondParams actor)
                {
                    Core.Resolve<MemApiTarget>().GetNearbyGameObjects(100, 分身);
                    if ((int)actor.id == 407)
                    {
                        var target = 分身.Values.FirstOrDefault(obj => obj.EntityId == actor.entityId);
                        if (target != null)
                        {
                            if(target.Position.X == 100 && target.Position.Z == 155) //上分身
                            {
                                switch(actor.arg0)
                                {
                                    case (4566):
                                        LogHelper.Print("先上分身右刀");
                                        map.AddDangerShape(randomUint, new DangerShape(0, duration, new RectShape()
                                        {
                                        Origin = new Vector3(90, 0, 165), //覆盖左半
                                        First = new Vector3(80, 0, 150),
                                        Second = new Vector3(80, 0,180),
                                        Third = new Vector3(100, 0, 180),
                                        Fourth = new Vector3(100,0,150)

                                        }));
                                        break;
                                    case (4568):
                                        LogHelper.Print("先上分身左刀");
                                        map.AddDangerShape(randomUint, new DangerShape(0, duration, new RectShape()
                                        {
                                            Origin = new Vector3(110, 0, 165), //覆盖右半
                                            First = new Vector3(100, 0, 150),
                                            Second = new Vector3(100, 0, 180),
                                            Third = new Vector3(120, 0, 180),
                                            Fourth = new Vector3(120, 0, 150)

                                        }));
                                        break;
                                    case (4567):
                                        LogHelper.Print("后上分身右刀");
                                        map.AddDangerShape(randomUint, new DangerShape(delay, lateDuration, new RectShape()
                                        {
                                            Origin = new Vector3(90, 0, 165), //覆盖左半
                                            First = new Vector3(80, 0, 150),
                                            Second = new Vector3(80, 0, 180),
                                            Third = new Vector3(100, 0, 180),
                                            Fourth = new Vector3(100, 0, 150)

                                        }));
                                        break;
                                    case (4569):
                                        LogHelper.Print("后上分身左刀");
                                        map.AddDangerShape(randomUint, new DangerShape(delay, lateDuration, new RectShape()
                                        {
                                            Origin = new Vector3(110, 0, 165), //覆盖右半
                                            First = new Vector3(100, 0, 150),
                                            Second = new Vector3(100, 0, 180),
                                            Third = new Vector3(120, 0, 180),
                                            Fourth = new Vector3(120, 0, 150)

                                        }));
                                        break;
                                }
                            }
                            else if(target.Position.X == 100 && target.Position.Z == 175) //下分身
                            {
                                switch (actor.arg0)
                                {
                                    case (4566):
                                        LogHelper.Print("先下分身右刀");
