namespace Amatsukaze.ScriptTest.零式.M4S.本体
{
    public class M4S午夜 : ITriggerScript
    {
        private Dictionary<uint, IGameObject> 分身 = new Dictionary<uint, IGameObject>();

        private Vector3 下月环分散 = new Vector3(95.5f,0,179);
        private Vector3 下月环分摊 = new Vector3(100,0,177.5f);

        private Vector3 下十字分散 = new Vector3(96f,0,177.5f);
        private Vector3 下十字分摊 = new Vector3(100,0,176.5f);

        private Vector3 侧月环分散 = new Vector3(93.4f,0,178);
        private Vector3 侧月环分摊 = new Vector3(90.5f,0,174.5f);

        private Vector3 侧十字分散 = new Vector3(93.8f, 0, 176.5f);
        private Vector3 侧十字分摊 = new Vector3(91.6f, 0, 173.5f);

        private bool 先分摊 = false;
        private bool 先分散 = false;

        private bool 先正点十字 = false;
        private bool 先侧点十字 = false;

        private bool 先正点月环 = false;
        private bool 先侧点月环 = false;

        private bool 后正点十字 = false;
        private bool 后侧点十字 = false;

        private bool 后正点月环 = false;
        private bool 后侧点月环 = false;

        private int delay = 8000;
        private int count = 0;


        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            //4561 先直线
            //4562 后直线
            //4563 先月环
            //4564 后月环

            if (condParams is ActorControlCondParams actor)
            {
                Core.Resolve<MemApiTarget>().GetNearbyGameObjects(100, 分身);
                if ((int)actor.id == 407)
                {
                    var target = 分身.Values.FirstOrDefault(obj => obj.EntityId == actor.entityId);
                    if (target != null)
                    {

                        //LogHelper.Print($"分身位置X: {target.Position.X}, Z: {target.Position.Z}, EntityID: {target.EntityId}");
                        switch(actor.arg0)
                        {
                            case 4561:
                                if(target.Position.X == 85)  //正点
                                {
                                    LogHelper.Print("先正点十字");
                                    先正点十字 = true;

                                }
                                else if(target.Position.X is > 89 and < 90 && target.Position.Z is > 154 and < 155)
                                {
                                    LogHelper.Print("先侧点十字");
                                    先侧点十字 = true;

                                }
                                break;
                            case 4562:
                                if (target.Position.X == 85)  //正点
                                {
                                    LogHelper.Print("后正点十字");
                                    后正点十字 = true;

                                }
                                else if (target.Position.X is > 89 and < 90 && target.Position.Z is > 154 and < 155)
                                {
                                    LogHelper.Print("后侧点十字");
                                    后侧点十字 = true;

                                }
                                break;
                            case 4563:
                                if (target.Position.X == 85)  //正点
                                {
                                    LogHelper.Print("先正点月环");
                                    先正点月环 = true;

                                }
                                else if (target.Position.X is > 89 and < 90 && target.Position.Z is > 154 and < 155)
                                {
                                    LogHelper.Print("先侧点月环");
                                    先侧点月环 = true;

                                }
                                break;
                            case 4564:
                                if (target.Position.X == 85)  //正点
                                {
                                    LogHelper.Print("后正点月环");
                                    后正点月环 = true;

                                }
                                else if (target.Position.X is > 89 and < 90 && target.Position.Z is > 154 and < 155)
                                {
                                    LogHelper.Print("后侧点月环");
                                    后侧点月环 = true;

                                }
                                break;
                        }

                    }

                }


            }
            else if(condParams is EnemyCastSpellCondParams spell)
            {
                if(spell.SpellId == 38443)
                {
                    LogHelper.Print("先分摊，再分散");
                    先分摊 = true;
                    count++;
                }
                else if (spell.SpellId == 38444)
                {
                    LogHelper.Print("先分散，再分摊");
                    先分散 = true;
                    count++;
                }
            }


            if(先正点十字)
            {
                if(先分摊)
                {
                    PositionHelper.MoveToPositionWithOffset(侧十字分摊, 0);
                    先正点十字 = false;
                    count++;
                }
                else if(先分散)
                {
                    PositionHelper.MoveToPositionWithOffset(侧十字分散, 0);
                    先正点十字 = false;
                    count++;
                }
            }
            else if (先侧点十字)
            {
                if (先分摊)
                {
                    PositionHelper.MoveToPositionWithOffset(下十字分摊, 0);
                    先侧点十字 = false;
                    count++;
                }
                else if (先分散)
                {
                    PositionHelper.MoveToPositionWithOffset(下十字分散, 0);
                    先侧点十字 = false;
                    count++;
                }
            }
            else if (先正点月环)
            {
                if (先分摊)
                {
                    PositionHelper.MoveToPositionWithOffset(下月环分摊, 0);
                    先正点月环 = false;
                    count++;
                }
                else if (先分散)
                {
                    PositionHelper.MoveToPositionWithOffset(下月环分散, 0);
                    先正点月环 = false;
                    count++;
                }
            }
            else if (先侧点月环)
            {
                if (先分摊)
                {
                    PositionHelper.MoveToPositionWithOffset(侧月环分摊, 0);
                    先侧点月环 = false;
                    count++;
                }
                else if (先分散)
                {
                    PositionHelper.MoveToPositionWithOffset(侧月环分散, 0);
                    先侧点月环 = false;
                    count++;
                }
            }

            if (后正点十字)
            {
                if (先分摊)
                {
                    DelayMove(delay, 侧十字分散);
                    后正点十字 = false;
                    count++;
                }
                else if (先分散)
                {
                    DelayMove(delay, 侧十字分摊);
                    后正点十字 = false;
                    count++;
                }
            }
            else if (后侧点十字)
            {
                if (先分摊)
                {
                    DelayMove(delay,下十字分散);
                    后侧点十字 = false;
                    count++;
                }
                else if (先分散)
                {
                    DelayMove(delay, 下十字分摊);
                    后侧点十字 = false;
                    count++;
                }
            }
            else if (后正点月环)
            {
                if (先分摊)
                {
                    DelayMove(delay, 下月环分散);
                    后正点月环 = false;
                    count++;
                }
                else if (先分散)
                {
                    DelayMove(delay, 下月环分摊);
                    后正点月环 = false;
                    count++;
                }
            }
            else if (后侧点月环)
            {
                if (先分摊)
                {
                    DelayMove(delay, 侧月环分散);
                    后侧点月环 = false;
                    count++;
                }
                else if (先分散)
                {
                    DelayMove(delay, 侧月环分摊);
                    后侧点月环 = false;
                    count++;
                }
            }

            if(count == 3)
            {
                return true;
            }
            return false;
        }

        private async Task DelayMove(int ms, Vector3 position)
        {
            await Coroutine.Instance.WaitAsync(ms);

            PositionHelper.MoveToPositionWithOffset(position, 0);


        }
    }
}

