namespace Amatsukaze.ScriptTest.零式.M4S.本体
{
    public class M4S特技2 : ITriggerScript
    {
        private Vector3 side = new Vector3(87, 0, 165);
        private Vector3 mid = new Vector3(100, 0, 165);

        private float offset = 0.3f;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            if(condParams is EnemyCastSpellCondParams spell)
            {
                if(spell.SpellId == 38416)
                {
                    LogHelper.Print("打中间，两边躲避");
                    DelayMoveSide();                              
                    return true;
                }
                else if(spell.SpellId == 38418)
                {

                    LogHelper.Print("打两边，中间躲避");
                    DelayMoveMid();                                    
                    return true;
                }
            }

            return false;
        }

        private async Task DelayMoveSide()
        {
            await Coroutine.Instance.WaitAsync(1500);

            PositionHelper.MoveToPositionWithOffset(side, offset);

            await Coroutine.Instance.WaitAsync(5000);

            PositionHelper.MoveToPositionWithOffset(mid, offset);
        }

        private async Task DelayMoveMid()
        {
            await Coroutine.Instance.WaitAsync(1500);

            PositionHelper.MoveToPositionWithOffset(mid, offset);
        }
    }
}

