namespace Amatsukaze.ScriptTest.零式.M4S.本体
{
    public class M4S本体黄蓝Buff : ITriggerScript
    {

        private uint yellowBuff = 4000u;
        private uint blueBuff = 4001u;
        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {

            if (!scriptEnv.KV.ContainsKey("本体黄Buff"))
            {
                scriptEnv.KV.Add("本体黄Buff", false);
            }
            else if (!scriptEnv.KV.ContainsKey("本体蓝Buff"))
            {
                scriptEnv.KV.Add("本体蓝Buff", false);
            }

            if (Core.Me.HasAura(yellowBuff))
            {
                LogHelper.Print("获得黄Buff");
                scriptEnv.KV["本体黄Buff"] = true;
                return true;
            }
            else if (Core.Me.HasAura(blueBuff))
            {
                LogHelper.Print("获得蓝Buff");
                scriptEnv.KV["本体蓝Buff"] = true;
                return true;
            }

            return false;
        }
    }
}

