using AEAssist.Helper;
using AEAssist.MemoryApi;
using AEAssist.Extension;
using Amatsukaze.ScriptTest.Helpers;
using Dalamud.Game.ClientState.Objects.Types;
using AEAssist.Avoid;

namespace Amatsukaze.ScriptTest.零式.M4S.本体
{
    public class M4S剑舞 : ITriggerScript
    {

        private Vector3 bottomSafe = new Vector3(92.3f, 0, 177.8f);
        private Vector3 leftSafe = new Vector3(83.2f, 0, 165.2f);

        private Vector3 mid = new Vector3(100, 0, 165);


        private float offset = 0.2f;


        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            if (condParams is not EnemyCastSpellCondParams spell)
            {
                return false;
            }

            switch (spell.SpellId)
            {
                case 38393:
                    LogHelper.Print("劈上面，左边和下面安全，d3去下面");
                    MoveBottom();
                    break;
                case 38394:
                    LogHelper.Print("劈中间，上面和下面安全，d3去下面");
                    MoveBottom();
                    break;
                case 38395:
                    LogHelper.Print("劈下面，左边和上面安全，d3去左边");
                    MoveLeft();
                    break;

            }


            return false;
        }

        private async Task MoveLeft()
        {
            await Coroutine.Instance.WaitAsync(8500);

            PositionHelper.MoveToPositionWithOffset(leftSafe, offset);

            await Coroutine.Instance.WaitAsync(6000);

            PositionHelper.MoveToPositionWithOffset(mid, 1);
        }

        private async Task MoveBottom()
        {
            await Coroutine.Instance.WaitAsync(8500);

            PositionHelper.MoveToPositionWithOffset(bottomSafe, offset);

            await Coroutine.Instance.WaitAsync(6000);

            PositionHelper.MoveToPositionWithOffset(mid, 1);
        }
    }
}
